import React, { useState } from "react";
import * as Components from './Components';
import WeatherPage from './WeatherPage';

function App() {
  const [signIn, toggle] = useState(true);
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    password: ""
  });
  const [loggedIn, setLoggedIn] = useState(false);

  const handleSignUp = (e) => {
    e.preventDefault();
    // Save user information to local storage
    localStorage.setItem('user', JSON.stringify(userData));
    alert("Signup successful!");
  };

  const handleSignIn = (e) => {
    e.preventDefault();
    // Retrieve user information from local storage
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser && storedUser.email === userData.email && storedUser.password === userData.password) {
      alert("Login successful!");
      setLoggedIn(true);
    } else {
      alert("Invalid credentials. Please try again.");
    }
  };

  if (loggedIn) {
    return <WeatherPage user={userData} />;
  }

  return (
    <Components.Container>
      <Components.SignUpContainer signinIn={signIn}>
        <Components.Form onSubmit={handleSignUp}>
          <Components.Title>Create Account</Components.Title>
          <Components.Input type='text' placeholder='Name' onChange={(e) => setUserData({ ...userData, name: e.target.value })} />
          <Components.Input type='email' placeholder='Email' onChange={(e) => setUserData({ ...userData, email: e.target.value })} />
          <Components.Input type='password' placeholder='Password' onChange={(e) => setUserData({ ...userData, password: e.target.value })} />
          <Components.Button type="submit">Sign Up</Components.Button>
        </Components.Form>
      </Components.SignUpContainer>

      <Components.OverlayContainer signinIn={signIn}>
        <Components.Overlay signinIn={signIn}>
          <Components.LeftOverlayPanel signinIn={signIn}>
            <Components.Title>Welcome Back!</Components.Title>
            <Components.Paragraph>
              To keep connected with us please login with your personal info
            </Components.Paragraph>
            <Components.GhostButton onClick={() => toggle(true)}>
              Sign In
            </Components.GhostButton>
          </Components.LeftOverlayPanel>

          <Components.RightOverlayPanel signinIn={signIn}>
            <Components.Title>Hello, Friend!</Components.Title>
            <Components.Paragraph>
              Enter Your personal details and start the journey with us
            </Components.Paragraph>
            <Components.GhostButton onClick={() => toggle(false)}>
              Sign Up
            </Components.GhostButton>
          </Components.RightOverlayPanel>
        </Components.Overlay>
      </Components.OverlayContainer>
    </Components.Container>
  );
}

export default App;
